import React from "react";

const index = ({ users }) => {
    return <div>{users.email}</div>;
};

index.getInitialProps = async (ctx) => {
    const res = await fetch("http://localhost:3000/api/users");
    const json = await res.json();

    return { users: json };
};
export default index;
