import React, { useState, useEffect } from "react";
import List from "../components/List";
import ListItem from "../components/ListItem";
import Select from "../components/Select";
import Router from "next/router";
import getHour from "../customFunctions/getHour";
let words = [
    { name: "Jastrzębie zdrój", id: 48 },
    { name: "Rybnik", id: 44 },
];

const index = ({ resData, wordID }) => {
    const word = words.find((word) => {
        return word.id == wordID;
    });

    const [data, setData] = useState(resData);

    useEffect(() => {
        setData(resData);
    }, [resData]);

    let arrayOfItems = Object.values(data);
    let dates = Object.keys(data);

    let lists = arrayOfItems.map((i, index) => {
        const items = i.map((e, eindex) => {
            if (e.practice) {
                return (
                    <ListItem key={eindex}>
                        {" "}
                        <b>Praktyka</b> : {getHour(e.practice[0].date)} - Miejsca:{" "}
                        {e.practice[0].places}
                    </ListItem>
                );
            }
            if (e.theory)
                return <ListItem key={eindex}> Teoria: {getHour(e.theory.date)}</ListItem>;
        });

        return (
            <List key={index} day={dates[index]}>
                {items}
            </List>
        );
    });
    const handleChange = (event) => {
        event.persist();
        Router.push({
            pathname: "/",
            query: { id: event.target.value },
        });
    };
    for (let i = 6; i < 12; i++) {}
    return (
        <div>
            {word.name}
            <Select
                onChange={handleChange}
                value={wordID}
                type="word"
                title="Wybór worda"
                options={words}
            />
            {lists}
        </div>
    );
};

export async function getServerSideProps(ctx) {
    ctx.query.id ? ctx.query.id : (ctx.query.id = 48);

    let resData = {};
    for (let i = 6; i < 12; i++) {
        const url = `https://info-car.pl/services/word/ajax/getSchedule?wordId=${ctx.query.id}&examCategory=B&month=2020-0${i}&_=1591863562242`;
        const res = await fetch(url);
        const json = await res.json();
        resData = { ...resData, ...json.terms };
    }

    return { props: { resData, wordID: ctx.query.id } };
}
export default index;
